import React, { Component, useEffect, useState } from 'react';
import './Home.css';
import axios from 'axios';
import SpeechRecognition, { useSpeechRecognition } from 'react-speech-recognition';
import Footer from '../components/Footer';
import Header from '../components/Header';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCaretDown } from '@fortawesome/free-solid-svg-icons';
import { Dropdown, DropdownButton } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

const Home = (props) => {
  const {
    transcript,
    listening,
    browserSupportsSpeechRecognition,
    isMicrophoneAvailable,
  } = useSpeechRecognition();
  const navigate = useNavigate();

  const [userTranscript, setUserTranscript] = useState('');
  const [isDropDownExpanded, setDropDownExpanded] = useState(false);
  const [selectedExample, setSelectedExample] = useState('');

  useEffect(() => {
    setUserTranscript(transcript);
  }, [transcript]);

  if (!browserSupportsSpeechRecognition || !isMicrophoneAvailable) {
    return <span>Browser doesn't support speech recognition.</span>;
  }

  function handleChange(event) {
    setUserTranscript(event.target.value);
  }

  function handleDropdownClick(text) {
    setSelectedExample(text);
    setUserTranscript(text);
    setDropDownExpanded(false);
  }

  function handleSubmit(event) {
    if (userTranscript.trim().length > 0) {
      navigate('/recipe-voice-bot/search_recipe');
      localStorage.setItem('userTranscript', userTranscript);
    }
    setUserTranscript('');
  }

  function resetTranscript() {
    SpeechRecognition.stopListening();
    setUserTranscript('');
  }

  return (
    <div>
      <Header />
      <div className='main'>
        <p className='helpStyle'>
          <span className='ask'>Ask for Recipes by</span>
          <span>
            <a
              className={`dropdown ${isDropDownExpanded ? 'expanded' : ''}`}
              id='a1'
              onClick={() => setDropDownExpanded(!isDropDownExpanded)}
              style={{ textDecoration: 'none', color: 'black' }}
            >
              &nbsp;&nbsp; &nbsp; &nbsp; Cuisine(Country){' '}
              <FontAwesomeIcon className='fa' icon={faCaretDown}></FontAwesomeIcon>
              {isDropDownExpanded && (
                <div class='dropdown-content'>
                  <p>Examples:</p>
                  {cuisineExamples.map((example, index) => (
                    <p key={index} onClick={() => handleDropdownClick(example)}>
                      {example}
                    </p>
                  ))}
                </div>
              )}
            </a>
          </span>
          {/* Add similar dropdowns for Ingredients, Ingredient Category, Cooking Process, and Utensils */}
        </p>

        <button className='buttonstyle buttonmargin1' onClick={SpeechRecognition.startListening}>
          SPEAK
        </button>

        <button className='buttonstyle buttonmargin2' onClick={resetTranscript}>
          CLEAR
        </button>

        <h2 className='Right-margin'>{listening ? 'Listening...' : ''}</h2>

        <form onSubmit={handleSubmit} className='centerdiv'>
          <label>
            <textarea
              type='text'
              value={userTranscript}
              onChange={handleChange}
              placeholder='Click SPEAK Button to ask queries to RecipeDB...'
              className='textareastyle'
            />
          </label>
        </form>

        <button className='buttonstyle buttonmargin2' onClick={handleSubmit}>
          SUBMIT
        </button>
      </div>
      <Footer />
    </div>
  );

}
export default Home;
