import React, { Component, useEffect, useState } from 'react';
import './Home.css';
import axios from 'axios';
import SpeechRecognition, { useSpeechRecognition } from 'react-speech-recognition';
import Footer from '../components/Footer';
import Header from '../components/Header';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCaretDown } from '@fortawesome/free-solid-svg-icons';
import { Dropdown, DropdownButton } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

const Home = (props) => {
  const {
    transcript,
    listening,
    browserSupportsSpeechRecognition,
    isMicrophoneAvailable,
  } = useSpeechRecognition();
  const navigate = useNavigate();

  const [userTranscript, setUserTranscript] = useState('');
  const [isDropDownExpanded, setDropDownExpanded] = useState(false);
  const [selectedExample, setSelectedExample] = useState('');

  useEffect(() => {
    setUserTranscript(transcript);
  }, [transcript]);

  if (!browserSupportsSpeechRecognition || !isMicrophoneAvailable) {
    return <span>Browser doesn't support speech recognition.</span>;
  }

  function handleChange(event) {
    setUserTranscript(event.target.value);
  }

  function handleDropdownClick(text) {
    setSelectedExample(text);
    setUserTranscript(text);
    setDropDownExpanded(false);
  }

  function handleSubmit(event) {
    if (userTranscript.trim().length > 0) {
      navigate('/recipe-voice-bot/search_recipe');
      localStorage.setItem('userTranscript', userTranscript);
    }
    setUserTranscript('');
  }

  function resetTranscript() {
    SpeechRecognition.stopListening();
    setUserTranscript('');
  }

  return (
    <div>
      <Header />
      <div className='main'>
        <p className='helpStyle'>
          <span className='ask'>Ask for Recipes by</span>
          <span>
  <a
    className={`dropdown ${isIngredientsExpanded ? 'expanded' : ''}`}
    id='a2'
    onClick={() => setDropDownExpanded(!isIngredientsExpanded)}
    style={{ textDecoration: 'none', color: 'black' }}
  >
    &nbsp;&nbsp; &nbsp; &nbsp; Ingredients{' '}
    <FontAwesomeIcon className='fa' icon={faCaretDown}></FontAwesomeIcon>
    {isIngredientsExpanded && (
      <div class='dropdown-content'>
        <p>Examples:</p>
        <p onClick={() => handleDropdownClick("Show me some recipes having handfuls fusilli")}>
          Show me some recipes having handfuls fusilli
        </p>
        <p onClick={() => handleDropdownClick("Can you suggest recipes that use bacon?")}>
          Can you suggest recipes that use bacon?
        </p>
        <p onClick={() => handleDropdownClick("I'd like to try cooking with almond, can you show me some recipes?")}>
          I'd like to try cooking with almond, can you show me some recipes?
        </p>
      </div>
    )}
  </a>
</span>
<span>
  <a
    className={`dropdown ${isIngredientCategoryExpanded ? 'expanded' : ''}`}
    id='a3'
    onClick={() => setDropDownExpanded(!isIngredientCategoryExpanded)}
    style={{ textDecoration: 'none', color: 'black' }}
  >
    &nbsp;&nbsp; &nbsp; &nbsp; Ingredient Category{' '}
    <FontAwesomeIcon className='fa' icon={faCaretDown}></FontAwesomeIcon>
    {isIngredientCategoryExpanded && (
      <div class='dropdown-content'>
        <p>Examples:</p>
        <p onClick={() => handleDropdownClick("Can you give me some recipes which require cooking in a microwave?")}>
          Can you give me some recipes which require cooking in a microwave?
        </p>
        <p onClick={() => handleDropdownClick("Show me some recipes which don't need an oven")}>
          Show me some recipes which don't need an oven
        </p>
        <p onClick={() => handleDropdownClick("Can you recommend some recipes which don't need refrigeration")}>
          Can you recommend some recipes which don't need refrigeration
        </p>
      </div>
    )}
  </a>
</span>

          {/* Add similar dropdowns for Ingredients, Ingredient Category, Cooking Process, and Utensils */}
        </p>

        <button className='buttonstyle buttonmargin1' onClick={SpeechRecognition.startListening}>
          SPEAK
        </button>

        <button className='buttonstyle buttonmargin2' onClick={resetTranscript}>
          CLEAR
        </button>

        <h2 className='Right-margin'>{listening ? 'Listening...' : ''}</h2>

        <form onSubmit={handleSubmit} className='centerdiv'>
          <label>
            <textarea
              type='text'
              value={userTranscript}
              onChange={handleChange}
              placeholder='Click SPEAK Button to ask queries to RecipeDB...'
              className='textareastyle'
            />
          </label>
        </form>

        <button className='buttonstyle buttonmargin2' onClick={handleSubmit}>
          SUBMIT
        </button>
      </div>
      <Footer />
    </div>
  );  
}
export default Home;
