import React, { Component, useEffect, useState }  from 'react';
import './Home.css';
import axios from 'axios'
import SpeechRecognition, { useSpeechRecognition } from 'react-speech-recognition'; // this is a library for speech recognition which help in converting speech to text
import Footer from '../components/Footer';
import Header from '../components/Header';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCaretDown, faCoffee } from '@fortawesome/free-solid-svg-icons'
import Dropdown from 'react-bootstrap/Dropdown';
import DropdownButton from 'react-bootstrap/DropdownButton';
import { Navigate } from 'react-router-dom';
import {  useNavigate } from 'react-router-dom';
const Home = (props) =>{
    const {
        transcript,
        listening,
        browserSupportsSpeechRecognition,
        isMicrophoneAvailable
    } = useSpeechRecognition();
    const navigate = useNavigate(); // Get the navigate function

    const [userTranscript, setUserTranscipt]  = useState("")
    const [isDropDownExpanded, setDropDownExpanded] = useState("none")


    useEffect(()=>{
        setUserTranscipt(transcript)
    },[transcript])

    if (!browserSupportsSpeechRecognition || !isMicrophoneAvailable) {
        return <span>Browser doesn't support speech recognition.</span>;
    }

    function handleChange(event){
        setUserTranscipt(event.target.value)
    }
    function changeDisplay(event){
        const parent = event.target
        if(parent.tagName=='A'){
            const div = (parent.getElementsByTagName("div"));
            if(isDropDownExpanded=="none"){
                setDropDownExpanded("block")
                div[0].style.display="block";
            }
            else{
                setDropDownExpanded("none")
                div[0].style.display="none";
            }
        }
    }
    function handleDropdownClick(text){ // this function is used to set the transcript to the text of the dropdown  
        setUserTranscipt(text)
    }


    function handleSubmit(event){
        console.log("user wants to say : ",userTranscript)
        if(userTranscript.trim().length > 0){
            navigate('/recipe-voice-bot/search_recipe')
            localStorage.setItem('userTranscript',userTranscript);
        }
        setUserTranscipt("")

    }

    function resetTranscript(){
        SpeechRecognition.stopListening()
        setUserTranscipt("");
    }

    return (
        <div>
          <Header></Header>
          <div className="main">
            <p className="helpStyle">
              <span className="ask"></span>
              <span>
                <a
                  className="dropdown"
                  id="a1"
                  onClick={changeDisplay}
                  style={{ textDecoration: 'None', color: '' }}
                >
                  &nbsp;&nbsp; &nbsp; &nbsp; Cuisine(Country){' '}
                  <FontAwesomeIcon
                    onClick={changeDisplay}
                    className="fa"
                    icon={faCaretDown}
                  ></FontAwesomeIcon>
                  <div class="dropdown-content">
                    <p>Examples:</p>
                    <p
                      onClick={() =>
                        handleDropdownClick('Recommend some Indian dishes to cook.')
                      }
                    >
                      Recommend some Indian dishes to cook.
                    </p>
                    <p
                      onClick={() =>
                        handleDropdownClick(
                          "I'm interested in trying out Lebanese cuisine, could you suggest some recipes?"
                        )
                      }
                    >
                      I'm interested in trying out Lebanese cuisine, could you suggest some recipes?
                    </p>
                    <p
                      onClick={() =>
                        handleDropdownClick('Can you recommend some Thai dishes to cook?')
                      }
                    >
                      Can you recommend some Thai dishes to cook?
                    </p>
                    <p
                      onClick={() =>
                        handleDropdownClick('I want to cook something with chicken, broccoli, and garlic')
                      }
                    >
                      I want to cook something with chicken, broccoli, and garlic
                    </p>
                    <p
                      onClick={() =>
                        handleDropdownClick(
                          "I'm interested in trying out Lebanese cuisine, could you suggest some recipes?"
                        )
                      }
                    >
                      I'm interested in trying out Lebanese cuisine, could you suggest some recipes?
                    </p>
                  </div>
                </a>
              </span>
            </p>
            <div className="container">
              <div className="row">
                <div className="col-md-12 home-screen align-items-center justify-content-center">
                  <img
                    src="https://www.google.com/images/branding/googlelogo/1x/googlelogo_color_272x92dp.png"
                    alt="Google Logo"
                  />
                  <div className="search-box col-md-7 border d-flex py-2 justify-content-between align-items-center">
                    <i className="fa fa-search"></i>
                    {term ? (
                      <i className="fa fa-close" onClick={() => resetTranscript()}></i>
                    ) : (
                      ''
                    )}
                    <i
                      className="fa fa-microphone"
                      onClick={SpeechRecognition.startListening}
                    ></i>
                  </div>
                  <div className="buttons col-md-4 ml-5 mt-4 align-items-center justify-content-center">
                    <input
                      type="button"
                      className="btn btn-light mx-1"
                      value="Google Search"
                      onClick={() => handleSubmit()}
                    />
                    <input
                      type="button"
                      className="btn btn-light"
                      value="I'm Feeling Lucky"
                    />
                  </div>
                </div>
              </div>
            </div>
            <Footer />
          </div>
        </div>
      );
    };
    export default Home;
