import React, { Component, useEffect, useState } from 'react';
import './Home.css';
import axios from 'axios';
import SpeechRecognition, { useSpeechRecognition } from 'react-speech-recognition';
import Footer from '../components/Footer';
import Header from '../components/Header';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCaretDown } from '@fortawesome/free-solid-svg-icons';
import { useNavigate } from 'react-router-dom';

const Home = (props) => {
  const {
    transcript,
    listening,
    browserSupportsSpeechRecognition,
    isMicrophoneAvailable
  } = useSpeechRecognition();
  const navigate = useNavigate();

  const [userTranscript, setUserTranscript] = useState('');
  const [term, setTerm] = useState('');

  useEffect(() => {
    setUserTranscript(transcript);
  }, [transcript]);

  if (!browserSupportsSpeechRecognition || !isMicrophoneAvailable) {
    return <span>Browser doesn't support speech recognition.</span>;
  }

  function handleChange(event) {
    setUserTranscript(event.target.value);
  }

  function openVoiceSearch() {
    SpeechRecognition.startListening();
  }

  function clearTerm() {
    setTerm('');
  }

  function handleSubmit(event) {
    event.preventDefault();
    console.log('user wants to say:', userTranscript);
    if (userTranscript.trim().length > 0) {
      navigate('/recipe-voice-bot/search_recipe');
      localStorage.setItem('userTranscript', userTranscript);
    }
    setUserTranscript('');
  }

  function handleSearch() {
    // Implement your search functionality using the 'term' state.
    console.log('Searching for:', term);
  }

  return (
    <div>
      <Header />
      <div className="main">
        <p className="helpStyle">
          <span className="ask"></span>
          <span>
            {/* Implement your dropdown options here */}
          </span>
        </p>

        <div className="container">
          <div className="row">
            <div className="col-md-12 home-screen align-items-center justify-content-center">
              <img
                src="https://www.google.com/images/branding/googlelogo/1x/googlelogo_color_272x92dp.png"
                alt="Google Logo"
              />
              <div className="search-box col-md-7 border d-flex py-2 justify-content-between align-items-center">
                <i className="fa fa-search"></i>
                <form className="form-search" onSubmit={(e) => handleSubmit(e)}>
                  <input
                    type="text"
                    name="term"
                    id="term"
                    value={term}
                    onChange={(e) => setTerm(e.target.value)}
                    placeholder="Click SPEAK Button to ask queries to RecipeDB..."
                  />
                </form>
                {term ? (
                  <i className="fa fa-close" onClick={() => clearTerm()}></i>
                ) : (
                  ''
                )}
                <i
                  className="fa fa-microphone"
                  onClick={() => openVoiceSearch()}></i>
              </div>
              <div className="buttons col-md-4 ml-5 mt-4 align-items-center justify-content-center">
                <input
                  type="button"
                  className="btn btn-light mx-1"
                  value="Google Search"
                  onClick={() => handleSearch()}
                />
                <input
                  type="button"
                  className="btn btn-light"
                  value="I'm Feeling Lucky"
                />
              </div>
            </div>
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default Home;
